package leetcode.medium;
/*
 * Given a binary string s (a string consisting only of '0's and '1's), we can split s 
 * into 3 non-empty strings s1, s2, s3 (s1+ s2+ s3 = s).Return the number of ways s can 
 * be split such that the number of characters '1' is the same in s1, s2, and s3. Since 
 * the answer may be too large, return it modulo 10^9 + 7.
 */
/*
 * Thoughts
 * special cases, 
 * 1. cannot divide S into 3 substrings with equivalent 1s 
 * 2. all 0s in s
 * 3. for cases that S has 1s that can be divided by 3, count the 0s in between 1 groups, then multiply them
 * 
 * Note: 
 * integer overflow issue
 */
public class NumberOfWaysSplitString {
    public static int numWays(String s) {
        int res=0;
        int modulo=(int) (Math.pow(10, 9)+7);
        int l=s.length(), total=0, p1=0, n1=0,n2=0,count=0;
        
        for(int i=0; i<l; i++) {
        	if(s.charAt(i)=='1') total++; 
        }
        
        if(total!=0 && total%3!=0) return 0;
        if(total==0) return (int)(((long)(l-1)*(long)(l-2)/2)%modulo);
        
        int num=total/3;
        for(int i=0; i<l; i++) {
        	if(s.charAt(i)=='1') {
        		count++;
        		if(count==num)p1=i;
        		else if(count==(num+1)) n1=i-p1;   
        		
        		if(count==num*2) p1=i;
        		else if(count==(num*2+1)) n2=i-p1;
        	}
        }
        res=(int)((((long)n1*(long)n2))%modulo);
        return res;
    }
    
    public static void main(String args[]) {
    	String s="";
    	System.out.print(numWays(s));
    }
}